ruleSearch.bookList:
.list li<js>
result
  .toArray()
  .slice(0, 5)
  .map(function (el) {
    var url = String(el.select('a').eq(0).attr('href')).replace(
      /^\/([^\/])/,
      baseUrl.match(/(.+\/\/.+?\/)/)[1] + '$1'
    );
    var J = org.jsoup.Jsoup.parse(String(java.ajax(url)));
    var og = (selector) =>
      J.select('[property="og:' + selector + '"]').attr('content');

    return {
      name: og('novel:book_name'),
      author: og('novel:author'),
      kind: og('novel:category') + ',' + og('novel:status'),
      latest: og('novel:latest_chapter_name') + ' • ' + String(og('novel:update_time')).replace(/(\d+:\d+)/,''),
      cover: og('image').replace('https','http'),
      wordCount: J.select('.layui-bg-red').text(),
      intro: J.select('.intro p').eq(0).text(),
      url:url,
    };
  });
</js>

.library li
<js>
result.toArray().slice(0, 3).map(el => {
	var url = String(el.select('a:eq(0)').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
	var J = org.jsoup.Jsoup.parse(java.ajax(url));
	var o = selector => String(J.select(selector).text());
	var og = selector => String(J.select('[property="og:' + selector + '"]').attr('content'));
	return {
		name: og('novel:book_name').replace(/(全文|小说|免费阅读|最新章节).*|[(（].*[）)]/g, ''),
		author: og('novel:author'),
		kind: og('novel:category') + ',' + og('novel:status').replace(/中|已/, ''),
     wordCount: o('.detail p:eq(2)').replace(/.*：/,''),
		latest: og('novel:latest_chapter_name').replace(/^(正文|VIP章节|最新章节)?(\s+|_)|[\(（【].*[求更谢乐发推].*/g, '') + ' • ' + og('novel:update_time').replace(/(T|\s).*/, ' ').replace(/\//g, '-'),
		intro: og('description').replace(/.*(观看小说|简介)[:：]|各位书友.*/g, '').replace(/\s+/g, ''),
		cover: og('image'),
		url: url,
	};
}); 
</js>

